mapper.select:
SELECT setmeal.*,category.name AS categoryName FROM setmeal setmeal LEFT OUTER JOIN category category ON setmeal.category_id = category.id

    and setmeal.name like concat('%',#{name},'%')
    and category_id = #{categoryId}
    and setmeal.status = #{status}

order by create_time Desc

mapper.update:
update setmeal
category_id = #{categoryId}, name = #{name}, price = #{price}, description = #{description}, status = #{status}, update_time = #{updateTime}, update_user = #{updateUser},
where id = #{id}
